                    {% endfor %}
                </select>
                {% if form.compagnie_maritime.errors %}
                <div class="alert alert-danger">
                    {% for error in form.compagnie_maritime.errors %}
                        <span style="font-size: 13px;">{{ error|escape }}</span>
                    {% endfor %}
                </div>   
                {% endif %}
            </div>
            <div class="form-row">
            <div class="form-group col-md-4">
                <label for="site">Site</label>
                <select name="site" id="id_site" class="form-control">
                    {% for option in  form.site.field.queryset %}
                        <option value="{{ option.id }}"> {{option}}</option>
                    {% endfor %}
                </select>
                {% if form.site.errors %}
                <div class="alert alert-danger">
                    {% for error in form.site.errors %}
                        <span style="font-size: 13px;">{{ error|escape }}</span>
                    {% endfor %}
                </div>   
                {% endif %}
            </div>
            <div class="form-group col-md-4">
                <label for="agent_selection">agent de selection</label>
                <select name="agent_selection" id="id_agent_selection" class="form-control">
                    {% for option in  form.agent_selection.field.queryset %}
                        <option value="{{ option.id }}"> {{option}}</option>
                    {% endfor %}
                </select>
                {% if form.agent_selection.errors %}
                <div class="alert alert-danger">
                    {% for error in form.agent_selection.errors %}
                        <span style="font-size: 13px;">{{ error|escape }}</span>
                    {% endfor %}
                </div>   
                {% endif %}
            </div>
            <div class="form-group col-md-4">
                <label for="agent_acconage">Agent de pose & Acconage</label>
                <select name="agent_acconage" id="id_agent_acconage" class="form-control">
                    {% for option in  form.agent_acconage.field.queryset %}
                        <option value="{{ option.id }}"> {{option}}</option>
                    {% endfor %}
                </select>
                {% if form.agent_acconage.errors %}
                <div class="alert alert-danger">
                    {% for error in form.agent_acconage.errors %}
                        <span style="font-size: 13px;">{{ error|escape }}</span>
                    {% endfor %}
                </div>   
                {% endif %}
            </div>
            </div>
            <hr class="my-4">
            <div class="form-row">
            <div class="col-md-6">

            </div>
            <div class="col-md-6 text-right">
                <button class="btn btn-primary" type="submit" >enregister</button>
            </div>
            </div>
        </form>
        </div> <!-- .col-12 -->
    </div> <!-- .row -->
    </div> <!-- .container-fluid -->


{% endblock content %}
